    return(
        <div>
            <If test={usuario && React.trim(usuario.nome)}>
                Seja bem vindo <strong>{props.usuario.nome} </strong>
            </If>

            <If test={!usuario || !usuario.nome}>
                Seja bem vindo AMIG√ÉO
            </If>
        </div>
    )
}
