import React, {useState} from 'react'

export default props =>{


    const [valor, setValor] = useState('Inicial');

    function changeValue(e){
        setValor(e.target.value);
    }


    return(
        <div>
            <h2>{valor}</h2>
            <input value={valor} onChange={changeValue} />
        </div>
    )
}
