import React,{useState} from "react";


export default props =>{

    function gerarNumeroNaoContido(min, max, array){
        const aleatorio = parseInt(Math.random() * (max + 1 - min)) + min;
        return array.includes(aleatorio) ? 
            gerarNumeroNaoContido(min, max, array) : 
            aleatorio
    }
    
    
    function geraNumeros(qtde){
        const numeros = Array(qtde)
        .fill(0).reduce((nums) =>{
            const novoNumero = gerarNumeroNaoContido(1, 60, nums);
            return [...nums, novoNumero]
        }, [])
        .sort((n1,  n2)=>n1-n2);
    
        return numeros
    }
    

    const [qtde, setQtde] = useState(props.qtde || 6);
    const numeroIniciais = Array(qtde).fill(0);
    const [numeros, setNumero] = useState([numeroIniciais])


    return(
        <div>
            <h2>Mega Sena</h2>
            <h3>{numeros.join(' ')}</h3>
            <div>
                <label>Qtde de Némeros</label>
                <input type="number" value={setQtde()} />
            </div>
            <button onClick={_=> setNumero(geraNumeros(qtde))}>
                Gerar Números
            </button>
        </div>
    );
}
